	public static int convertOctaltoDecimal(int n , int b ) {
		int rv = 0;
		int p = 1;
		while(n>0) {
			int dig = n % 10;
			n = n / 10;
			rv += dig * p;
			p = p* b;
		}
		return rv;
	}

}

