#include <cmath>
using namespace std;

int weeksToMinutes(int weeks){
    return (weeks*10080);
}

int main(){
    int n;
    cout << "Enter number of weeks: ";
    cin >> n;
    cout << "Minutes: " << weeksToMinutes(n) << endl;
    return 0;
}

