console.log("That is " + FindSurfaceAreaOfACuboid(2, 3, 4) + " // 52");

console.log("Area of cuboid of dimensions 5, 1, 3: ");
console.log("That is " + FindSurfaceAreaOfACuboid(5, 1, 3) + " // 46");

