function getHundreds(number) {
  if (number > 0 && number < 10) {
    return getOnes(number);
  }
  if (number >= 10 && number < 20) {
    return getTeens(number % 10);
  }
  if (number >= 20 && number < 100) {
    return `${getTens(Math.floor(number / 10))} ${getOnes(number % 10)}`;
