sub reverse_number {
    my $num = shift;
    if ($num < 10) {
        return $num;
    }
    else {
        my $last_digit = $num % 10;
        my $remaining_num = int($num / 10);
        return $last_digit . reverse_number($remaining_num);
    }
}

