#define _POSIX_C_SOURCE 200809L /* for getline*/

#include <stdio.h> /* printf */
#include <stdlib.h> /* size_t, free */
#include <string.h> /* strlen */
#include <unistd.h> /* getline */

void PrintReverseOfAString(char *);


int main()
{
    size_t size = 100;

    char *str = NULL;

    getline(&str,&size,stdin);

    PrintReverseOfAString(str);

    free(str);

    return 0;
}

void PrintReverseOfAString(char *str)
{
    int size = strlen(str);
    int i = 0;

    for(i = size;i >= 0; --i)
    {
        printf("%c",str[i]);
    }
}

c-plus-plus:
#include <iostream>
#include <string>

void SWAP(char& x, char& y){
	char t = x;
	x = y;
	y = t;
}

void PrintReverseOfAString(std::string s){
	int n = s.length();
	for(int i=0, j=n-1; i<j; i++, j--){
		SWAP(s[i], s[j]);
	}
	std::cout<<s<<std::endl;
}

int main(){
	std::string s;
	std::cin>>s;
	PrintReverseOfAString(s);
	return 0;
}

